    //
    //
    // get main navigation items from wp backend
    function get_main_nav() {
        return wp_get_nav_menu_items('main-nav');
    }

    add_action( 'rest_api_init', function () {
        register_rest_route( 'routes', '/main', array(
            'methods' => 'GET',
            'callback' => 'get_main_nav',
        ) );
    });

    //
    //
    // make custom endpoint for getting only the images and ids of the projects pod items
    function get_commission_projects() {
        $params = array(
            'limit' => -1,
            'where' => 'project_type.slug = "commission" '
        );

        $pods = pods( 'project', $params );
        $projectIds = array();

        if ( $pods->total() > 0 ) {
            while ( $pods->fetch() ) {
                array_push(
                    $projectIds,
                    array(
                        "id" => $pods->field( 'id' ),
                        "image" => $pods->field( 'project_image' )
                    )
                );
            }
        }

        return $projectIds;
    }

    add_action( 'rest_api_init', function() {
        register_rest_route( 'pods', '/commissions', array(
            'methods' => 'GET',
            'callback' => 'get_commission_projects'
        ) );
    } );

    //
    //
    //
    function get_wedding_projects() {
        $params = array(
            'limit' => -1,
            'where' => 'project_type.slug = "wedding" '
        );

        $pods = pods( 'project', $params );
        $projectIds = array();

        if ( $pods->total() > 0 ) {
            while ( $pods->fetch() ) {
                array_push(
                    $projectIds,
                    array(
                        "id" => $pods->field( 'id' ),
                        "image" => $pods->field( 'project_image' )
                    )
                );
            }
        }

        return $projectIds;
    }

    add_action( 'rest_api_init', function() {
        register_rest_route( 'pods', '/weddings', array(
            'methods' => 'GET',
            'callback' => 'get_wedding_projects'
        ) );
    } );

